import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import styled from 'styled-components';
import Navigation from './components/Navigation';
import Dashboard from './components/Dashboard';
import TemplateList from './components/Templates/TemplateList';
import TemplateForm from './components/Templates/TemplateForm';
import RecipientList from './components/Recipients/RecipientList';
import RecipientForm from './components/Recipients/RecipientForm';
import CampaignForm from './components/EmailCampaign/CampaignForm';
import { theme } from './theme';

const AppContainer = styled.div`
  background-color: ${props => props.theme.backgroundColor};
  color: ${props => props.theme.textColor};
  min-height: 100vh;
`;

const MainContent = styled.main`
  padding: 20px;
`;

function App() {
  return (
    <ThemeProvider theme={theme}>
      <AppContainer>
        <Router>
          <Navigation />
          <MainContent>
            <Switch>
              <Route exact path="/" component={Dashboard} />
              <Route exact path="/templates" component={TemplateList} />
              <Route path="/templates/new" component={TemplateForm} />
              <Route path="/templates/:id" component={TemplateForm} />
              <Route exact path="/recipients" component={RecipientList} />
              <Route path="/recipients/new" component={RecipientForm} />
              <Route path="/recipients/:id" component={RecipientForm} />
              <Route path="/campaign" component={CampaignForm} />
            </Switch>
          </MainContent>
        </Router>
      </AppContainer>
    </ThemeProvider>
  );
}

export default App;
