import React from 'react';

const templates = [
  { id: 1, title: 'Welcome Email', content: 'Welcome to our service!' },
  { id: 2, title: 'Thank You Email', content: 'Thank you for your purchase!' },
];

const TemplateList: React.FC = () => {
  return (
    <div className="bg-white shadow-md rounded p-4 mt-4">
      <h2 className="text-gray-700 text-lg font-bold mb-4">Saved Templates</h2>
      <ul>
        {templates.map((template) => (
          <li key={template.id} className="border-b border-gray-200 py-2">
            <div className="font-bold text-blue-500">{template.title}</div>
            <p className="text-gray-700">{template.content}</p>
            <button className="text-blue-700 hover:underline mt-2">Preview</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TemplateList;
